var counter = 0;
var data = JSON.parse(responseBody);
var pattern = new RegExp("^[0-9a-fA-F]{8}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{4}-[0-9a-fA-F]{12}$");
data.forEach(function(uuid) {
   var result = pattern.test(uuid);
   if (result) {
       counter++;
   }
   postman.setEnvironmentVariable("statementId", uuid);
});

tests["Body contains 10 UUID's"] = counter === 10;

